def test(testSubject):
    white ={"W":0,
            "B":0,
            "D":0}
    black ={"W":0,
            "B":0,
            "D":0}
    for whiteGame in range(50):
        print(whiteGame)
        outcome = Game(testSubject, Player(depth=searchDepth).randomise().createOffspring(), display=False, movelimit=moveLimit).play()
        white[outcome]+=1
        testSubject.maxdepth = searchDepth
    for blackGame in range(50):
        print(blackGame)
        outcome = Game(Player(depth=searchDepth).randomise().createOffspring(), testSubject, display=False, movelimit=moveLimit).play()
        black[outcome]+=1
        testSubject.maxdepth = searchDepth
    return black, white

